<span class="line highlighted"><span style="color:#C586C0;">    else</span><span style="color:#C586C0;"> if</span><span style="color:#D4D4D4;"> (</span><span style="color:#9CDCFE;">session</span><span style="color:#D4D4D4;">.</span><span style="color:#9CDCFE;">status</span><span style="color:#D4D4D4;"> === </span><span style="color:#CE9178;">&quot;authenticated&quot;</span><span style="color:#D4D4D4;">)</span></span>
